mapper.select:
	SELECT /* KRF-BIZ.ChartMapper.getRWMDT */
           COL_KOR AS "column",
   		   CONT  AS "cont"
	  FROM KRF_SITEINFO 
	  WHERE SITE_CD = #{recordId}

	  AND GUBUN = #{parentId}

WITH TMP_TBL AS ( SELECT /* KRF-BIZ.ChartMapper.getRWMDTSelectA */ RANK( ) OVER(PARTITION BY A.PT_NO||ITEM_NAME ORDER BY WMCYMD DESC) RN , A.PT_NO, PT_NM, A.WMYR, A.WMOD, WMWK, WMCYMD, ITEM_NAME , CASE WHEN SUBSTR(ITEM_VALUE, 1, 1) = '.' THEN 0||ITEM_VALUE ELSE ITEM_VALUE END ITEM_VALUE FROM ( SELECT PT_NO, WMYR, WMOD, WMCD , CASE WHEN ITCD = '1052' THEN 'ITEM_BOD' WHEN ITCD = '1054' THEN 'ITEM_DOC' WHEN ITCD = '1049' THEN 'ITEM_COD' WHEN ITCD = '1055' THEN 'ITEM_TN' WHEN ITCD = '1056' THEN 'ITEM_TP' WHEN ITCD = '1060' THEN 'ITEM_TEMP' WHEN ITCD = '1039' THEN 'ITEM_PH' WHEN ITCD = '1053' THEN 'ITEM_SS' WHEN ITCD = '1063' THEN 'ITEM_CLOA' END ITEM_NAME , TO_CHAR(WMVL) AS ITEM_VALUE , TO_CHAR(WMVL) AS ITEM_VALUE_1 FROM TW_RWMDT WHERE ITCD IN ('1052', '1054', '1049', '1055', '1056', '1060', '1039', '1053', '1063') ) A , RWMPT B , ( SELECT DISTINCT PT_NO , WMYR , WMOD , SUBSTR(WMWK, 1, 1)||'회차' AS WMWK , WMCYMD FROM RWMDTD ) C WHERE A.PT_NO = B.PT_NO AND A.PT_NO = C.PT_NO AND A.WMYR = C.WMYR AND A.WMOD = C.WMOD AND A.WMCD = SUBSTR(C.WMWK, 1, 1) AND C.WMCYMD IS NOT NULL AND A.PT_NO = #{recordId}
AND A.WMYR||A.WMOD BETWEEN #{preFullDate} AND #{nextFullDate}
AND ITEM_NAME = #{selectItem} AND ITEM_VALUE IS NOT NULL ORDER BY WMCYMD, ITEM_NAME ) SELECT RN, PT_NO, PT_NM, WMYR, WMOD, WMWK, WMCYMD, ITEM_NAME, TO_NUMBER(ITEM_VALUE) AS ITEM_VALUE , REPLACE(ITEM_VALUE, '999999999', '정량한계미만' ) AS ITEM_VALUE_1 FROM ( SELECT * FROM TMP_TBL
WHERE RN <= 10
ORDER BY WMCYMD ) UNION ALL SELECT 999 as RN, '', '', '', '', '', '', '', MAX(TO_NUMBER(REPLACE(TO_NUMBER(ITEM_VALUE), 999999999, 0))) + MAX(TO_NUMBER(REPLACE(TO_NUMBER(ITEM_VALUE), 999999999, 0))) / 10, '' FROM TMP_TBL
WHERE RN <= 10

WITH TMP_TBL AS ( SELECT RANK () OVER (PARTITION BY PT_NO ORDER BY WMYR||WMOM||WMOD DESC) RN , VGBN, RAGBN, PT_NO, PT_NM , WMYR , DECODE(WMWK,'1','상반기','2','하반기') AS WMWK , WMOM, WMOD, WMCTM , MCNT , ${selectItem} AS ITEM_NAME , ${selectItem} AS ITEM_VALUE FROM ( SELECT * FROM( SELECT V.* FROM CE_SDM_RWMDTI_DAY_DAE_V V WHERE V.VGBN='S' AND V.PT_NO = #{recordId}
AND V.WMYRWK BETWEEN #{preFullDate} AND #{nextFullDate}
) ORDER BY WMYR, WMWK, ORD_PT_NM, ORD_GBN ) ORDER BY WMYR ASC ,WMWK ASC ) SELECT RN, PT_NO, PT_NM, TO_CHAR(WMYR) AS WMYR, WMWK AS WMWK, WMYR||' '||WMWK AS WMCYMD, ITEM_NAME , TO_CHAR(REPLACE (ITEM_VALUE, '정량한계미만',999999999)) AS ITEM_VALUE, TO_CHAR(ITEM_VALUE) AS ITEM_VALUE_1 FROM TMP_TBL
WHERE RN <= 10
UNION ALL SELECT 999 AS RN, '', '', '', '', '', '', CASE WHEN SUBSTR(TO_CHAR(MAX (TO_NUMBER (REPLACE (TRIM(ITEM_VALUE), '정량한계미만',0)))*1.1),1,1) = '.' THEN '0'||TO_CHAR(MAX (TO_NUMBER (REPLACE (TRIM(ITEM_VALUE), '정량한계미만',0)))*1.1) ELSE TO_CHAR(MAX (TO_NUMBER (REPLACE (TRIM(ITEM_VALUE), '정량한계미만',0)))*1.1) END, '' FROM TMP_TBL
WHERE RN <= 10

WITH TMP_TBL /* KRF-BIZ.ChartMapper.getRWMDTSelectD005 */ AS (SELECT RANK() OVER( PARTITION BY STNID||ITEM_NAME ORDER BY STNID, WMCYMD DESC) AS RN /* 순번 */, STNID AS PT_NO, OBSNM AS PT_NM, WMCYMD, ITEM_NAME, ITEM_VALUE FROM (SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'WD' AS ITEM_NAME, ROUND(AVG(WD / 10), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'WS' AS ITEM_NAME, ROUND(AVG(WS / 10), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'TA' AS ITEM_NAME, ROUND(AVG(TA / 10), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'HM' AS ITEM_NAME, ROUND(AVG(HM), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'PA' AS ITEM_NAME, ROUND(AVG(PA), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'PS' AS ITEM_NAME, ROUND(AVG(PS), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'RNYN' AS ITEM_NAME, ROUND(AVG(RNYN), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'RN1HR' AS ITEM_NAME, ROUND(AVG(RN1HR / 10), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8) UNION ALL SELECT STNID, SUBSTR(TM, 1, 8) AS WMCYMD, 'RNDAY' AS ITEM_NAME, ROUND(AVG(RNDAY / 10), 2) AS ITEM_VALUE FROM WTAWSMST GROUP BY STNID, SUBSTR(TM, 1, 8)) A, WTOBSIF B WHERE A.STNID = B.WTOBSCD AND OBSTCD = '2' AND A.STNID = #{recordId}
AND SUBSTR(WMCYMD, 1, 6) BETWEEN '201410' AND '201510'
AND SUBSTR(WMCYMD, 1, 6) BETWEEN #{preFullDate} AND #{nextFullDate}
AND ITEM_NAME = #{selectItem} AND ITEM_VALUE IS NOT NULL ORDER BY WMCYMD ASC) SELECT * FROM (SELECT * FROM TMP_TBL
WHERE RN <= 10
) UNION ALL SELECT 9999 AS RN, '', '', '', '', MAX(ITEM_VALUE) + MAX(ITEM_VALUE) / 10 FROM TMP_TBL
WHERE RN <= 10

WITH TMP_TBL /* KRF-BIZ.ChartMapper.getRWMDTSelectF_1 */ AS (SELECT RANK() OVER( PARTITION BY A.FACI_CD, A.DISCHARGE_NUM, ITEM_NAME ORDER BY A.FACI_CD, A.DISCHARGE_NUM, A.WORK_DT DESC) AS RN, A.FACI_CD AS PT_NO, FACI_NM AS PT_NM /* 처리시설명 */, REPLACE(A.WORK_DT, '-', '.') AS WMCYMD /* 운영일자 */, '방류구번호 : ' ||A.DISCHARGE_NUM AS DISCHARGE_NUM /* 방류구번호 */, ITEM_NAME, ITEM_VALUE FROM (SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('AMT_PHYS ') AS ITEM_NAME, DISCHARGE_AMT_PHYS AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('AMT_BIO ') AS ITEM_NAME, DISCHARGE_AMT_BIO AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('AMT_HIGHTEC ') AS ITEM_NAME, DISCHARGE_AMT_HIGHTEC AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('ITEM_BOD ') AS ITEM_NAME, BOD AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('ITEM_COD ') AS ITEM_NAME, COD AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('ITEM_SS ') AS ITEM_NAME, SS AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('ITEM_TN ') AS ITEM_NAME, TN AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('ITEM_TP ') AS ITEM_NAME, TP AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL UNION ALL SELECT YYYY, FACI_NM, FACI_CD, WORK_DT, DISCHARGE_NUM, TRIM('ITEM_COLI ') AS ITEM_NAME, COLI AS ITEM_VALUE FROM VPLA_FACI_OUT_TOTAL) A, (SELECT FACI_CD, WORK_DT, MAX(DISCHARGE_NUM) AS DISCHARGE_NUM FROM VPLA_FACI_OUT_TOTAL GROUP BY FACI_CD, WORK_DT) B WHERE A.FACI_CD = B.FACI_CD AND A.WORK_DT = B.WORK_DT AND A.DISCHARGE_NUM IN ( 1, NULL ) AND A.DISCHARGE_NUM = B.DISCHARGE_NUM AND A.FACI_CD = #{recordId}
