WITH TMP_TBL /* KRF-BIZ.ChartMapper.getRWMDTSelectF_4 */ AS (SELECT RANK() OVER( PARTITION BY A.FACI_CD, A.PIPE_NUM, ITEM_NAME ORDER BY A.FACI_CD, A.PIPE_NUM, A.WORK_DT DESC) AS RN, FACI_NM AS PT_NM, REPLACE(A.WORK_DT, '-', '.') AS WMCYMD /* 운영일자*/, '관거번호 : ' ||A.PIPE_NUM ||'(' ||PIPE_TYPE ||')' AS PIPE_NUM /* 관거번호(관거유형) */, ITEM_NAME, ITEM_VALUE FROM (SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_AMT' AS ITEM_NAME, AMT AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER UNION ALL SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_BOD' AS ITEM_NAME, BOD AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER UNION ALL SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_COD' AS ITEM_NAME, COD AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER UNION ALL SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_SS' AS ITEM_NAME, SS AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER UNION ALL SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_TN' AS ITEM_NAME, TN AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER UNION ALL SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_TP' AS ITEM_NAME, TP AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER UNION ALL SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_COLI' AS ITEM_NAME, COLI AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER UNION ALL SELECT FACI_CD, FACI_NM, WORK_DT, PIPE_NUM, PIPE_TYPE, 'ITEM_BYPASS_AMT' AS ITEM_NAME, BYPASS_AMT AS ITEM_VALUE FROM VPLA_FACI_PIPE_TRANSFER) A, (SELECT FACI_CD, WORK_DT, MAX(PIPE_NUM) AS PIPE_NUM FROM VPLA_FACI_PIPE_TRANSFER GROUP BY FACI_CD, WORK_DT) B WHERE A.FACI_CD = B.FACI_CD AND A.WORK_DT = B.WORK_DT AND A.PIPE_NUM IN ( 0, 1, NULL ) AND A.PIPE_NUM = B. PIPE_NUM AND A.FACI_CD = #{recordId}
