mapper.select:
WITH TMP_TBL AS ( SELECT /* KRF-BIZ.ChartMapper.getRWMDTSelectA */ RANK( ) OVER(PARTITION BY A.PT_NO||ITEM_NAME ORDER BY WMCYMD DESC) RN , A.PT_NO, PT_NM, A.WMYR, A.WMOD, WMWK, WMCYMD, ITEM_NAME , CASE WHEN SUBSTR(ITEM_VALUE, 1, 1) = '.' THEN 0||ITEM_VALUE ELSE ITEM_VALUE END ITEM_VALUE FROM ( SELECT PT_NO, WMYR, WMOD, WMCD , CASE WHEN ITCD = '1052' THEN 'ITEM_BOD' WHEN ITCD = '1054' THEN 'ITEM_DOC' WHEN ITCD = '1049' THEN 'ITEM_COD' WHEN ITCD = '1055' THEN 'ITEM_TN' WHEN ITCD = '1056' THEN 'ITEM_TP' WHEN ITCD = '1060' THEN 'ITEM_TEMP' WHEN ITCD = '1039' THEN 'ITEM_PH' WHEN ITCD = '1053' THEN 'ITEM_SS' WHEN ITCD = '1063' THEN 'ITEM_CLOA' END ITEM_NAME , TO_CHAR(WMVL) AS ITEM_VALUE , TO_CHAR(WMVL) AS ITEM_VALUE_1 FROM TW_RWMDT WHERE ITCD IN ('1052', '1054', '1049', '1055', '1056', '1060', '1039', '1053', '1063') ) A , RWMPT B , ( SELECT DISTINCT PT_NO , WMYR , WMOD , SUBSTR(WMWK, 1, 1)||'회차' AS WMWK , WMCYMD FROM RWMDTD ) C WHERE A.PT_NO = B.PT_NO AND A.PT_NO = C.PT_NO AND A.WMYR = C.WMYR AND A.WMOD = C.WMOD AND A.WMCD = SUBSTR(C.WMWK, 1, 1) AND C.WMCYMD IS NOT NULL AND A.PT_NO = #{recordId}
AND A.WMYR||A.WMOD BETWEEN #{preFullDate} AND #{nextFullDate}
AND ITEM_NAME = #{selectItem} AND ITEM_VALUE IS NOT NULL ORDER BY WMCYMD, ITEM_NAME ) SELECT RN, PT_NO, PT_NM, WMYR, WMOD, WMWK, WMCYMD, ITEM_NAME, TO_NUMBER(ITEM_VALUE) AS ITEM_VALUE , REPLACE(ITEM_VALUE, '999999999', '정량한계미만' ) AS ITEM_VALUE_1 FROM ( SELECT * FROM TMP_TBL
WHERE RN <= 10
ORDER BY WMCYMD ) UNION ALL SELECT 999 as RN, '', '', '', '', '', '', '', MAX(TO_NUMBER(REPLACE(TO_NUMBER(ITEM_VALUE), 999999999, 0))) + MAX(TO_NUMBER(REPLACE(TO_NUMBER(ITEM_VALUE), 999999999, 0))) / 10, '' FROM TMP_TBL
WHERE RN <= 10
