mapper.select:
    	SELECT MAX (WMYR || '.' || WMWK) AS WMYR
		  FROM SDM_RWMDTI
		 WHERE PT_NO IN  #{item}

select.if:
	SELECT MAX(SUBSTR(TO_CHAR(YMDH, 'YYYYMMDDHH24'), 1, 8)) AS WMCYMD 
      FROM WLDY 
	 WHERE WLOBSCD IN  #{item}

	SELECT SUBSTR (MAX(YMD), 1, 4)|| '.'|| SUBSTR (MAX(YMD), 5, 2)|| '.'|| SUBSTR (MAX(YMD), 7, 2) AS WMCYMD  
      FROM RFDY 
	 WHERE RFOBSCD IN  #{item}

SELECT MAX(TO_CHAR(YMDH, 'YYYY.MM.DD')) AS WMCYMD 
  FROM FWDY 
 WHERE WLOBSCD IN  #{item}

SELECT MAX(SUBSTR(YMD,1,8)) AS WMCYMD 
  FROM DMDY 
 WHERE DMOBSCD IN  #{item}

SELECT MAX(TM) AS WMCYMD 
  FROM WTAWSMST 
 WHERE STNID IN  #{item}

SELECT MAX(SUBSTR(TM,1,8)) AS WMCYMD 
  FROM RNDY 
 WHERE STN_ID IN  #{item}

SELECT MAX(YMDHM) AS WMCYMD 
  FROM BOMST 
 WHERE BOOBSCD IN   #{item}

SELECT MAX(WORK_DT) AS WORK_DT_VAL 
    FROM   VPLA_FACI_OUT_TOTAL 
  WHERE  FACI_CD IN  #{item}

SELECT  MAX(WORK_DT) AS WORK_DT_VAL 
    FROM   VPLA_FACI_DIRECT_TRANSFER 
  WHERE  FACI_CD IN  #{item}

SELECT MAX(WORK_DT) AS WORK_DT_VAL 
    FROM   VPLA_FACI_IN_TOTAL 
  WHERE  FACI_CD IN  #{item}

SELECT MAX(WORK_DT) AS WORK_DT_VAL 
    FROM   VPLA_FACI_PIPE_TRANSFER 
  WHERE  FACI_CD IN  #{item}

SELECT MAX (WMCYMD) AS WMCYMD
  FROM AG_RWMDTI_DAY_V_ALL_KRF
 WHERE PT_NO IN  #{item}

		SELECT MAX(WMCYMD) AS WMCYMD FROM (
             SELECT WMCYMD , PT_NO FROM AG_RWMDTI_DAY_V_KRF
             UNION
             SELECT WMCYMD , PT_NO FROM AG_RWMDTI_DAY_V_C_KRF
         ) WHERE PT_NO IN  #{item}
